const editButtons = document.getElementsByClassName("btn-edit");
// id_body is the id of the textarea for the comment content. Generated by crispy forms
const commentText = document.getElementById("id_body");
const commentForm = document.getElementById("commentForm");
const submitButton = document.getElementById("submitButton");

const deleteModal = new bootstrap.Modal(document.getElementById("deleteModal"));
const deleteButtons = document.getElementsByClassName("btn-delete");
const deleteConfirm = document.getElementById("deleteConfirm");

/**
* Initializes edit functionality for the provided edit buttons.
* 
* For each button in the `editButtons` collection:
* - Retrieves the associated comment's ID upon click.
* - Fetches the content of the corresponding comment.
* - Populates the `commentText` input/textarea with the comment's content for editing.
* - Updates the submit button's text to "Update".
* - Sets the form's action attribute to the `edit_comment/{commentId}` endpoint.
*/
for (let button of editButtons) {
  button.addEventListener("click", (e) => {
    // comment_id has its value set in post_detail, in the Edit button
    let commentId = e.target.getAttribute("comment_id");
    /* When the Edit button for a specific comment is clicked, the JavaScript code fetches the text from
    that comment and places it into the comment form */
    let commentContent = document.getElementById(`comment${commentId}`).innerText;
    // Now the text can be updated
    commentText.value = commentContent;
    submitButton.innerText = "Update";
    // Modifies the comment form's action attribute so Django knows which comment in the db it is updating
    commentForm.setAttribute("action", `edit_comment/${commentId}`);
  });
}

/**
* Initializes deletion functionality for the provided delete buttons.
* 
* For each button in the `deleteButtons` collection:
* - Retrieves the associated comment's ID upon click.
* - Updates the `deleteConfirm` link's href to point to the 
* deletion endpoint for the specific comment.
* - Displays a confirmation modal (`deleteModal`) to prompt 
* the user for confirmation before deletion.
*/
for (let button of deleteButtons) {
    button.addEventListener("click", (e) => {
      // comment_id has its value set in post_detail, in the Delete button
      let commentId = e.target.getAttribute("comment_id");
      // This ensures that the delete link on the modal's confirmation button is updated with the right comment ID
      deleteConfirm.href = `delete_comment/${commentId}`;
      deleteModal.show();
    });
  }